mapper.select:
SELECT 

FROM dict_areas a

WHERE a.id = #{id}

SELECT 

FROM dict_areas a




		AND a.areaid = #{areaid}


		AND a.area LIKE 
			'%'||#{area}||'%'
			'%'+#{area}+'%'
			concat('%',#{area},'%')


		AND a.cityid = #{cityid}




		ORDER BY ${page.orderBy}

SELECT 

FROM dict_areas a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE dict_areas SET 	
	id = #{id},
	areaid = #{areaid},
	area = #{area},
	cityid = #{cityid}
WHERE id = #{id}
