mapper.select:
SELECT 

FROM dict_cities a

WHERE a.id = #{id}

SELECT 

FROM dict_cities a




		AND a.cityid = #{cityid}


		AND a.city LIKE 
			'%'||#{city}||'%'
			'%'+#{city}+'%'
			concat('%',#{city},'%')


		AND a.provinceid = #{provinceid}




		ORDER BY ${page.orderBy}

SELECT 

FROM dict_cities a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE dict_cities SET 	
	id = #{id},
	cityid = #{cityid},
	city = #{city},
	provinceid = #{provinceid}
WHERE id = #{id}
